import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";
import Card from "../UI/Card";

function Expenses(props) {
  const [filteredSelection, setFilteredSelection] = useState("2020");
  const Selected = (select) => {
    setFilteredSelection(select);
  };
  const expenseFilter = props.data.filter((expenseFilter) => expenseFilter === filteredSelection)

  // Elijo un año del desplegable
  // Dependiendo de cuál sea
  // El map devuelve un array filtrado con el criterio de que ese año sea el mismo que el que devuelve props.data

  return (
    <Card className="expenses">
      <ExpensesFilter selected={filteredSelection} onSelected={Selected} />
      {props.expenseFilter.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
}

export default Expenses;
