import React, {useState} from "react";
import "./ExpenseForm.css";

/////////////////////////////////////////////////////////////////////////////////
//Dos posibilidades distintas de usar el State:
//una repitiendo el useState y cada una con su State, más código pero más clara
//y bastante usada. La otra más ordenada, usando sólo un state con un objeto pero
//menos usada y legible, aunque más eficiente.
/////////////////////////////////////////////////////////////////////////////////

const ExpenseForm = () => {
    // const [enteredTitle, setEnteredTitle] = useState('')
    // const [enteredAmount, setEnteredAmount] = useState('')
    // const [enteredDate, setEnteredDate] = useState('')
    const [userInput, setUserInput] = useState({
        enteredTitle: '',
        enteredAmount:'',
        enteredDate: ''
    })

    const titleChangeHandler = (event) => {
        // setEnteredTitle(event.target.value);
        // setUserInput({
        //      ...userInput,
        //      enteredTitle: event.target.value});
            setUserInput((prevState) => {
                return {...prevState, enteredTitle: event.target.value}
            })

    }
    const amountChangeHandler = (event) => {
        // setEnteredAmount(event.target.value);
        setUserInput({
            ...userInput,
            enteredAmount: event.target.value});
    }
    const dateChangeHandler = (event) => {
        // setEnteredDate(event.target.value);
        setUserInput({
            ...userInput,
            enteredDate: event.target.value});
    }


  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" onChange={amountChangeHandler} min='0.01' step='0.01'></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input onChange={dateChangeHandler} type="date" min="2019-01-01" max='2022-12-31'></input>
        </div>
      </div>
      <div className="new-expenses__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
