        setIsEditing(true)
    }
    return (
        <div className="new-expense">
            {!isEditing && <button onClick={startEditingHandler}>Add new expenses</button>}
            <ExpenseForm cancel={switchForm} onSaveExpenseData={saveExpenseDataHandler} />
        </div>
    )
}

