import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";
import Card from "../UI/Card";

function Expenses(props) {
  const [filteredSelection, setFilteredSelection] = useState("noFilter");
  const Selected = (select) => {
    setFilteredSelection(select);
  };

  let filteredData = props.data.filter(
    (expense) => expense.date.getFullYear().toString() === filteredSelection
  );
  if (filteredSelection === "noFilter") {
    filteredData = props.data;
  }

  let expensesContent = <p className="noExpenses">No expenses üéâ</p>;

  if (filteredData.length > 0) {
    expensesContent = filteredData.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ));
  }

  return (
    <Card className="expenses">
      <ExpensesFilter selected={filteredSelection} onSelected={Selected} />
      {expensesContent}
      {/* OTRA FORMA DE ESCRIBIRLO SER√çA:

      {filteredData.length === 0 && <p className="noExpenses">No expenses üéâ</p>}
      {filteredData.length > 0 &&
      filteredData.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))
      } */}

      {/* Y OTRA:

      {filteredData.length === 0 ? (
        <p className="noExpenses">No expenses üéâ</p>
      ) : (
        filteredData.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))
      )} */}
    </Card>
  );
}

export default Expenses;
