import React, {useState} from "react";
import './NewExpense.css';
import ExpenseForm from './ExpenseForm'



function NewExpense(props) {
    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        }
        props.onSendNewExpense(expenseData)

    }
    let showForm = false
    const [change, setChange] = useState(main)

    const switchForm = (main) => {
        showForm = !showForm
        if (showForm === false) {
            let main = <button onClick={switchForm}>Add new expenses</button>
        }
        else {
            setChange(
                <ExpenseForm cancel={switchForm} onSaveExpenseData={saveExpenseDataHandler} />
                )
        }
    }



    return (
        <div className="new-expense">
            {change}
        </div>
    )
}

export default NewExpense;
